import{_ as e,c as i,o as t,a2 as a}from"./chunks/framework.CEgrwLex.js";const f=JSON.parse('{"title":"Technical Specifications","description":"","frontmatter":{},"headers":[],"relativePath":"nl/p1meterkit/technical-specifications.md","filePath":"nl/p1meterkit/technical-specifications.md","lastUpdated":null}'),o={name:"nl/p1meterkit/technical-specifications.md"},n=a('<h1 id="technical-specifications" tabindex="-1">Technical Specifications <a class="header-anchor" href="#technical-specifications" aria-label="Permalink to &quot;Technical Specifications&quot;">​</a></h1><p>The P1MeterKit is designed to provide detailed energy consumption data by connecting directly to your energy meter. Below are the technical specifications of the device:</p><h2 id="general-specifications" tabindex="-1">General Specifications <a class="header-anchor" href="#general-specifications" aria-label="Permalink to &quot;General Specifications&quot;">​</a></h2><ul><li><strong>Dimensions</strong>: 92 x 47.5 x 26.5 mm</li></ul><h2 id="connectivity" tabindex="-1">Connectivity <a class="header-anchor" href="#connectivity" aria-label="Permalink to &quot;Connectivity&quot;">​</a></h2><ul><li><strong>Network Connection</strong>: Wi-Fi IEEE 802.11 b/g/n (2.4 GHz), supports WPA/WPA2 security</li><li><strong>Data Transfer</strong>: Update interval every 1 second</li></ul><h2 id="power-supply" tabindex="-1">Power Supply <a class="header-anchor" href="#power-supply" aria-label="Permalink to &quot;Power Supply&quot;">​</a></h2><ul><li><strong>Primary Power Source</strong>: RJ12 port for direct coupling with DSMR-compatible energy meters</li><li><strong>Alternative Power Source</strong>: USB-C port for external power supply (5V, 1A)</li><li><strong>Power Over RJ12</strong>: Supported for DSMR v5.0 meters</li></ul><h2 id="compatibility" tabindex="-1">Compatibility <a class="header-anchor" href="#compatibility" aria-label="Permalink to &quot;Compatibility&quot;">​</a></h2><ul><li><strong>Energy Meters</strong>: Compatible with DSMR versions 4.0 and 5.0, as well as ESMR 5.0</li><li><strong>Software</strong>: OpenSource ESPHome, fully integrated with Home Assistant, support for firmware updates (OTA)</li></ul><h2 id="environmental" tabindex="-1">Environmental <a class="header-anchor" href="#environmental" aria-label="Permalink to &quot;Environmental&quot;">​</a></h2><ul><li><strong>Operational Temperature Range</strong>: -10°C to +40°C</li><li><strong>Classification</strong>: IP20 (suitable for indoor use)</li></ul><h2 id="indicators" tabindex="-1">Indicators <a class="header-anchor" href="#indicators" aria-label="Permalink to &quot;Indicators&quot;">​</a></h2><ul><li><strong>LED 1 (White Light)</strong>: Indicates USB-C power connection</li><li><strong>LED 2 (Red Light)</strong>: Flashes if not connected to Wi-Fi or Home Assistant</li><li><strong>LED 3 (Yellow Light)</strong>: Indicates RJ12 power connection</li></ul><p>For more detailed technical information, please refer to the complete manual provided with your P1MeterKit or visit our <a href="https://p1meterkit.nl/en" target="_blank" rel="noreferrer">official website</a>.</p><p>Please note: We are not responsible for any issues arising from water damage or improper installation. Ensure the unit is installed in a dry, indoor environment.</p>',16),r=[n];function l(s,c,p,d,h,u){return t(),i("div",null,r)}const g=e(o,[["render",l]]);export{f as __pageData,g as default};
