import{_ as e,c as t,o,a2 as n}from"./chunks/framework.CEgrwLex.js";const b=JSON.parse('{"title":"Troubleshooting Guide","description":"","frontmatter":{},"headers":[],"relativePath":"nl/waterflowkit/troubleshooting.md","filePath":"nl/waterflowkit/troubleshooting.md","lastUpdated":null}'),i={name:"nl/waterflowkit/troubleshooting.md"},r=n('<h1 id="troubleshooting-guide" tabindex="-1">Troubleshooting Guide <a class="header-anchor" href="#troubleshooting-guide" aria-label="Permalink to &quot;Troubleshooting Guide&quot;">​</a></h1><p>Als u problemen ondervindt met de WaterFlowKit, probeer dan de volgende stappen:</p><h2 id="algemene-probleemoplossingsstappen" tabindex="-1">Algemene probleemoplossingsstappen <a class="header-anchor" href="#algemene-probleemoplossingsstappen" aria-label="Permalink to &quot;Algemene probleemoplossingsstappen&quot;">​</a></h2><ol><li><p><strong>Controleer of alle kabels goed zijn aangesloten</strong></p><ul><li>Controleer of de USB-C-voedingskabel stevig is aangesloten op zowel de WaterFlowKit als de voedingsadapter.</li><li>Zorg ervoor dat de waterflowsensor kabels correct zijn bevestigd en vastgeklikt in de connectoren.</li></ul></li><li><p><strong>Controleer de stroombron</strong></p><ul><li>Zorg ervoor dat de voedingsadapter is aangesloten op een werkend stopcontact.</li><li>Controleer of de voedingskabel niet beschadigd is en goed functioneert.</li></ul></li><li><p><strong>Herstart de WaterFlowKit</strong></p><ul><li>Trek de voedingskabel uit de WaterFlowKit, wacht 10 seconden en steek hem dan weer in.</li></ul></li><li><p><strong>Herstart Home Assistant</strong></p><ul><li>Soms kan een eenvoudige herstart van Home Assistant connectiviteits- of integratieproblemen oplossen. Ga naar <strong>Instellingen &gt; Systeem &gt; Herstarten</strong> binnen Home Assistant.</li></ul></li><li><p><strong>Update Home Assistant</strong></p><ul><li>Zorg ervoor dat u de nieuwste versie van Home Assistant gebruikt. Ga naar <strong>Instellingen &gt; Systeem &gt; Updates</strong> om te controleren op en eventuele beschikbare updates te installeren.</li></ul></li></ol><h2 id="wifi-connectiviteitsproblemen" tabindex="-1">WiFi-connectiviteitsproblemen <a class="header-anchor" href="#wifi-connectiviteitsproblemen" aria-label="Permalink to &quot;WiFi-connectiviteitsproblemen&quot;">​</a></h2><ol><li><p><strong>Controleer WiFi-signaalsterkte</strong></p><ul><li>Zorg ervoor dat de WaterFlowKit binnen bereik van uw WiFi-router is. Een zwak signaal kan connectiviteitsproblemen veroorzaken.</li><li>Indien nodig, verplaats de WaterFlowKit dichter bij de router of gebruik een WiFi-extender om het signaal te versterken.</li></ul></li><li><p><strong>Opnieuw verbinden met WiFi</strong></p><ul><li>Als de WaterFlowKit geen verbinding maakt met uw WiFi-netwerk, probeer dan opnieuw te verbinden: <ol><li>Schakel de WaterFlowKit uit.</li><li>Schakel hem weer in en maak verbinding met de WiFi-hotspot genaamd &#39;waterflowkit&#39; met het wachtwoord &#39;waterflowkit&#39;.</li><li>Volg de instructies op het scherm om uw WiFi-netwerk te selecteren en verbinding te maken.</li></ol></li></ul></li><li><p><strong>Controleer WiFi-netwerkconfiguratie</strong></p><ul><li>Zorg ervoor dat uw WiFi-netwerk is ingesteld op 2,4 GHz, aangezien de WaterFlowKit geen 5 GHz-netwerken ondersteunt.</li></ul></li></ol>',6),l=[r];function a(s,d,g,p,u,c){return o(),t("div",null,l)}const h=e(i,[["render",a]]);export{b as __pageData,h as default};
